import React, { Component } from "react";
import Todo from "./Todos/Todos";
import axios from "axios";

class TodoList extends Component {
  state = {
    loggedIn: this.props.logged,
    id: this.props.user,
    user: [
      {
        name: "rishabh",
        task: "Complete homework",
        deadline: "23/12/1996"
      },
      {
        userId: "prince",
        task: "Complete homework",
        deadline: "23/12/1996"
      }
    ]
  };

  render() {
    let show = "";
    const items = [];

    const data = {
      email: this.state.id
    };

    const getData = () => {

      if (this.state.loggedIn) {
        axios
          .post("http://localhost:8080/taskGet", data)
          .then(response => {
            console.log(response.data);
            for (const key of response.data) {
              console.log(key);
              items.push(<Todo task={key.task} deadline={key.deadline} />);
              items.push(<br></br>);
            }
          })
          .catch(error => console.log(console.error()));
      }
    };

    // {for (const key of this.state.user)
    //   {

    //     items.push(<Todo  task={key.task} deadline={key.deadline}/> )
    //     items.push(<br></br>)
    //   }

    if (!this.state.loggedIn) {
      items.push(<h1>PLease login first</h1>);
    }

    return (
      <React.Fragment>
        <div>Your Todos</div>
        {getData()}
        {items}
        <div className="wrapper">
          <button className="btn btn-lg btn-primary ">Add Todo</button>
        </div>
      </React.Fragment>
    );
  }
}

export default TodoList;
