import React, { Component } from "react";
import axios from "axios";

class SignUp extends Component {
  state = {
    accountMade: false,
    OTP: false,
    OtpValue: 0
  };

  otpValidate = event => {
    event.preventDefault();
    console.log(this.state)
    // this.setState({ OtpValue: event.target.otps.value });
    if(this.state.OtpValue==event.target.otps.value){
      console.log("yes")
      this.setState({ accountMade: true });
      this.forceUpdate();
      console.log(this.state)
    }
  };
  submitForm = event => {
    event.preventDefault();

    const data = {
      email: event.target.email.value,
      password: event.target.password.value
    };

    var xhr = new XMLHttpRequest();
    axios
      .post("http://localhost:8080/signup", data)
      .then(response => {
        if (response.data) {
          axios
            .post("http://localhost:8081/sendmail", data)
            .then(response1 => {
              console.log(response1.data);
              if (response1.status == 200) {
                this.setState({ OTP: true });
                this.setState({OtpValue:response1.data})
              }
            })
            .catch(error1 => console.log("send mail failed", error1));
          // if (this.state.OTP) {
          //   this.setState({ accountMade: true });
          // }
        } else {
          this.setState({ accountMade: false });
          alert("Accont already made...Kindly login");
        }
      })
      .catch(error => console.log(console.error()));
  };

  render() {
    var body = "";
    if (this.state.accountMade) {
      body = (
        <div>
          <h1>Account created successfully</h1>
          <h2>Please login</h2>
        </div>
      );
    }
    // if (this.state.OTP) {
    //   body = 
    //   <div>
    //     <br/><br/><br/>

    //     <form onSubmit={this.otpValidate}>
    //       <input
    //         type="text"
    //         id="lname"
    //         name="otps"
    //         placeholder="Enter OTP"
    //         required
    //       />
    //       <br/><br/>
    //       <input type="submit" value="Submit" />
    //     </form>
    //     </div>
  // }

     else {
      body = (
        <div>
          <div>
            <form onSubmit={this.submitForm}>
              <label for="fname">First Name</label>
              <br />
              <input
                type="text"
                id="fname"
                name="firstname"
                placeholder="Your name.."
              />
              <br />
              <br />

              <label for="lname">Last Name</label>
              <br />
              <input
                type="text"
                id="lname"
                name="lastname"
                placeholder="Your last name.."
              />
              <br />
              <br />

              <label for="country ">Country</label>
              <select id="country" name="country">
                <option value="India">Australia</option>
                <option value="canada">Canada</option>
                <option value="usa">USA</option>
              </select>
              <br />
              <br />

              <input
                type="text"
                id="lname"
                name="email"
                placeholder="Your Email"
                required
              />
              <br />

              <input
                type="password"
                id="lname"
                name="password"
                placeholder="Your Password"
                required
              />
              <br /> <br />
              <input
            type="text"
            id="lname"
            name="otps"
            placeholder="Enter OTP"
            required
            disabled
          />
           <br />

              <input type="submit" value="send Me OTP" onClick={this.otpValidate}/>
              <br/>
              <input hidden type="submit" value="Submit" />
            </form>
          </div>
        </div>
      );
    }

    return <React.Fragment>{body}</React.Fragment>;
  }
}
export default SignUp;
