import React, { Component } from "react";
import axios from 'axios';

class AddTodo extends Component {
    formHandler=(event) => {
        event.preventDefault()

        const data={
          email: event.target.task.value,
          password:event.target.deadline.value
        }

        var xhr = new XMLHttpRequest();
        axios.post("http://localhost:8080/signup",data)
        .then(response=>
          {
            if (response.data)
            {
              this.setState({accountMade:true})
            }
            else{
              this.setState({accountMade:false})
              alert("Accont already made...Kindly login")

            }

          })
        .catch(error=>console.log(console.error()))



        };






  render() {
    return (
      <React.Fragment>
        <form onSubmit={this.formHandler}>
          <div class="form-group">
            <label for="exampleInputEmail1">Task to be done</label>
            <input
              type="text"
              class="form-control"
              id="exampleInputEmail1"
              name="task"
              aria-describedby="emailHelp"
              placeholder="Enter task title"
            />
            <small id="emailHelp" class="form-text text-muted">
              We'll never share your email with anyone else.
            </small>
          </div>
          <div class="form-group">
            <label for="exampleInputPassword1">Deadline</label>
            <input
              type="date"
              class="form-control"
              name="deadline"
              id="exampleInputPassword1"
              placeholder="Password"
            />
          </div>

          <button type="submit" class="btn btn-primary">
            Save
          </button>
        </form>
      </React.Fragment>
    );
  }
}

export default AddTodo;
